      "file_path": "bots/$NAME/action-code/go_to_oak_log.js",
      "content": "(async (bot) => {
    try {
        let position = world.getNearestBlock(bot, 'oak_log', 20);
        if (position) {
            await skills.goToPosition(bot, position.x, position.y, position.z);

            if (bot.interrupt_code) {
                const errorMsg = 'Oak log navigation interrupted by yourself';
                log(bot, errorMsg);
                throw new Error(errorMsg);
            }

            log(bot, 'Arrived at oak log successfully');
        } else {
            const errorMsg = 'No oak log found nearby';
            log(bot, errorMsg);
            throw new Error(errorMsg);
        }
        return true;
    } catch (error) {
        const errorMsg = \`Oak log navigation failed: \${error.message}\`;
        log(bot, errorMsg);
        throw error;
    }
})"
    },
    {
      "name": "Execute",
